const DEFAULT_OPTIONS = {
    uses: ['antd', 'lodash'],
    mode: 'development',
    hot: false,
    hostType: 'application',
    polyfill: false,
    modules: false,
    displayName: 'auto',
    cwd: process.cwd(),
    srcDirectory: 'src',
    openInEditorPrefix: '',
};
export const fillBabelConfigOptions = (options) => {
    return { ...DEFAULT_OPTIONS, ...options };
};
export const shouldEnable = (library, config) => {
    return config.includes(library);
};
export const compatPluginTarget = (target) => {
    if (!target || typeof target !== 'object') {
        return target;
    }
    if ('default' in target) {
        return compatPluginTarget(target.default);
    }
    return target;
};
