const isPresent = (item) => item.index !== 'D' && item.workingTree !== 'D';
const isStaged = (item) => isPresent(item) && item.index !== '?' && item.index !== ' ';
const isStagedOnly = (item) => isStaged(item) && item.workingTree === ' ';
export const filterChanged = (items) => items.filter(isPresent);
export const filterStaged = (items) => items.filter(isStaged);
export const filterStagedOnly = (items) => items.filter(isStagedOnly);
export const extractName = (item) => {
    const name = (item.index === 'R' || item.index === 'C') ? item.path.split('->')[1].trim() : item.path;
    return (name.startsWith('"') && name.endsWith('"')) ? name.slice(1, -1) : name;
};
